            $('#current-blog .current-blog-title').text(res.blog_name);

            Hatena.Diary.Pages.Admin['globalheader'].initBlogDropdown(BASE);
            Hatena.Diary.Pages.Admin['globalheader'].initAdminDropdown(BASE);

